INSERT INTO `ts_lang` (`lang_id`, `key`, `appname`, `filetype`, `zh-cn`, `en`, `zh-tw`) VALUES(3, 'NOTIFY_AUDIT_ERROR_CONTENT', 'PUBLIC', 0, '抱歉，您的帐号注册申请未能通过审核，感谢您对[ {site} ]的支持。若对审核结果有疑问，请联系<a href="{kfemail}" >客服邮箱</a>。', 'Sorry, your account  registered application is not approved, thank you for your support. Any questions, please contact <a href="{kfemail}" >Service mailbox</a> ', '抱歉，您的帳號註冊申請未能通過審核，感謝您對[ {site} ]的支持。若對審核結果有疑問，請聯繫<a href="{kfemail}" >客服郵箱</a>。');
